<?phpnamespace Anime\Command;use Sequence\Command\ContainerAwareCommand;use Sequence\User\User;use Symfony\Component\Console\Input\InputInterface;use Symfony\Component\Console\Output\OutputInterface;class DeleteOldUsersCommand extends ContainerAwareCommand{    /**     * {@inheritdoc}     */    public function isEnabled()    {        return parent::isEnabled();    }    /**     * {@inheritdoc}     */    public function configure()    {        $this            ->setName('users:clearold')            ->setDescription('Remove inactive users by 1 year.');    }    public function execute(InputInterface $input, OutputInterface $output)    {        $userManager = $this->getContainer()->get('user_manager');        $users = $userManager->findUsers('last_login < "' . date('Y-m-d H:i:s', strtotime('-1 year')) . '"')->get()->fetchAll();        if($users)        {            foreach($users as $user)            {                $output->writeln($user['name']);                $userManager->deleteUser(new User($user));            }            return;        }        $output->writeln('No users to delete.');    }} 